INSERT INTO `managers` (`id`, `name`, `slug`, `avatar`, `description`, `function_time`, `active`, `created_at`, `updated_at`) VALUES
	(1, 'Jerónimo Gonga Cassule', 'jeronimo-gonga-cassule', NULL, '<p>Jerónimo Gonga Cassule foi o primeiro Administrador Municipal de Ambaca, depois da independência do país, governando de <strong>1976 a 1979</strong>.</p>', '1976 - 1979', 1, '2023-12-11 14:14:44', '2023-12-14 14:55:50'),
	(2, 'Antônio da Costa Miranda', 'antonio-da-costa-miranda', NULL, '<p>Antônio da Costa Miranda, exerceu o cargo de Administrador municipal de <strong>1979 </strong>a <strong>1982</strong>, tendo sucedido o ex-administrar Jerónimo Gonga Cassule.</p><p>&nbsp;</p>', '1979 - 1982', 1, '2023-12-11 14:26:34', '2023-12-11 15:42:31'),
	(3, 'Francisco dos Santos (Chico Santos)', 'francisco-dos-santos-chico-santos', NULL, '<p>Chico Santos, como era também conhecido, esteve à frente dos destinos do município por quatro (4) anos, exercendo o cargo de <strong>1982 </strong>a <strong>1986</strong>.</p>', '1982 - 1986', 1, '2023-12-11 14:27:42', '2023-12-11 15:42:41'),
	(4, 'Joaquim Ribeiro de Almeida (Dibuca)', 'joaquim-ribeiro-de-almeida-dibuca', NULL, '<p>Joaquim Ribeiro de Almeida, também conhecido por Dibuca, exerceu o cargo num período de 3 anos, desde <strong>1986 </strong>a <strong>1989</strong>.&nbsp;</p>', '1986 - 1989', 1, '2023-12-11 14:29:06', '2023-12-11 15:42:53'),
	(5, 'Jerónimo Gonga Cassule', 'jeronimo-gonga-cassule', NULL, '<p>Depois de ter ocupado o cargo de 1976 a 1979, como primeiro administrador do município,&nbsp; Jerónimo Gonga Cassule volta a assumir a função por ano, de <strong>1991 </strong>a <strong>1992</strong>.</p>', '1991 - 1992', 1, '2023-12-11 14:29:33', '2023-12-11 15:43:04'),
	(6, 'Francisco Vunda Domingos', 'francisco-vunda-domingos', NULL, '<p>Francisco Vunda Domingos, engarregou-se da função de Administrador municipal de Ambaca num período de 5 anos, de <strong>1997 </strong>a <strong>2002</strong>.</p>', '1997 - 2002', 1, '2023-12-11 14:29:54', '2023-12-11 15:44:29'),
	(7, 'Francisco Simão Caponde', 'francisco-simao-caponde', 'managers/x4jx2LbRPfNQFbjcA0VuPRPpB0fw23-metaMmV4LWFkbWluaXN0cmFkb3JfY29tdW5hbF9kb19sdWluZ2EtX3NpbWFvX2NhcG9udGUuanBn-.jpg', '<p>Francisco Caponde exerceu as funções de Administrador Municipal de Ambaca entre <strong>Maio de</strong> <strong>2002 </strong>e <strong>Outubro de 2004</strong>, assim como de 1º Secretário Municipal do MPLA, entre <strong>2004 </strong>e 2016.</p>', '2002 - 2004', 1, '2023-12-11 14:30:16', '2023-12-11 15:45:10'),
	(8, 'Geraldo Malungo Belengue', 'geraldo-malungo-belengue', NULL, '<p>Oitavo na sequência de administradores municipais, Geraldo Malungo Belengue este à frente da gestão do município de <strong>2002 </strong>a <strong>2006</strong>.</p>', '2004 - 2006', 1, '2023-12-11 14:30:38', '2023-12-12 13:44:51'),
	(9, 'José Mateus Augustinho Martins', 'jose-mateus-augustinho-martins', NULL, '<p>Durante três (3) anos, de 2006 a 2009, José Mateus Augustinho Martins este no comando administrativo da vida do município de Ambaca.&nbsp;&nbsp;</p>', '2006 - 2009', 1, '2023-12-11 14:31:06', '2023-12-12 13:46:56'),
	(10, 'José Francisco Barros Ranque Franque', 'jose-francisco-barros-ranque-franque', NULL, '<p>Com um dos períodos mais longos no quesito administrativo, Ranque Franque governou o município de Ambaca por oito (anos), mais precisamente de 2009 a 2017.</p>', '2009 - 2017', 1, '2023-12-11 14:31:32', '2023-12-12 13:48:42'),
	(11, 'Malundo Fausto Congo Catessamo', 'malundo-fausto-congo-catessamo', NULL, '<p>O Senhor Malundo Fausto Congo Catessamo esteve à frente dos destinos do município no período entre 2017 a 2020.</p>', '2017 - 2020', 1, '2023-12-11 14:31:57', '2023-12-12 13:50:28'),
	(12, 'Francisco Antonio Cambango', 'francisco-antonio-cambango', 'managers/uxcrSwSoYcfVNg8eNJErMzCGhyUJxM-metaRnJhbmNpc2NvIENhbWJhbmdvLmpwZw==-.jpg', '<p>Francisco António Cambango, actual Administrador municipal de Ambaca, é o 12º gestor do município, natural de Camabatela, e está no exercío da função administrativa desde 2021.</p>', '2021', 1, '2023-12-11 14:32:39', '2023-12-11 15:19:48');

